#![feature(test)]

extern crate test;

use regexpr::Regex;
use test::Bencher;

static HUGE_REGEX_A: &str = "";

static HUGE_REGEX_B: &str = "";

#[bench]
fn compile(b: &mut Bencher) {
    let huge_regex = format!("({HUGE_REGEX_A}|{HUGE_REGEX_B})");
    b.iter(|| {
        Regex::compile(&huge_regex).unwrap();
    });
}

#[bench]
fn matches(b: &mut Bencher) {
    let huge_regex = format!("({HUGE_REGEX_A}|{HUGE_REGEX_B})");
    let regex = Regex::compile(&huge_regex).unwrap();
    b.iter(|| {
        assert!(regex.test(HUGE_REGEX_A));
        assert!(regex.test(HUGE_REGEX_B));
    });
}

#[bench]
fn non_match(b: &mut Bencher) {
    let huge_regex = format!("({HUGE_REGEX_A}|{HUGE_REGEX_B})");
    let regex = Regex::compile(&huge_regex).unwrap();
    b.iter(|| {
        assert!(!regex.test("abclkaj"));
    });
}

#[bench]
fn non_match_huge(b: &mut Bencher) {
    let huge_regex = format!("({HUGE_REGEX_A}|{HUGE_REGEX_B})");
    let regex = Regex::compile(&huge_regex).unwrap();
    b.iter(|| {
        assert!(!regex.test(""));
    });
}

#[bench]
fn non_match_strict_start_end(b: &mut Bencher) {
    let huge_regex = format!("^({HUGE_REGEX_A}|{HUGE_REGEX_B})$");
    let regex = Regex::compile(&huge_regex).unwrap();
    b.iter(|| {
        assert!(!regex.test("abclkaj"));
    });
}

#[bench]
fn non_match_huge_strict_start_end(b: &mut Bencher) {
    let huge_regex = format!("^({HUGE_REGEX_A}|{HUGE_REGEX_B})$");
    let regex = Regex::compile(&huge_regex).unwrap();
    b.iter(|| {
        assert!(!regex.test(""));
    });
}
